import create from 'zustand';

interface DemoState {
    idx: number;
    setIdx: (idx: number) => void;
    value: number;
    setValue: (value: number) => void;
}

const useStore = create<DemoState>((set) => ({
    idx: 0,
    setIdx: (idx) =>
        set((state) => ({
            ...state,
            idx
        })),
    value: 0,
    setValue: (value) =>
        set((state) => ({
            ...state,
            value
        }))

}));

export default useStore;
