import create from 'zustand';

interface DemoState {
    idx: number;
    value: number;
    increment: (by: number) => void;
    decrement: (by: number) => void;
}

const useStore = create<DemoState>((set) => ({
    idx: 0,
    value: 0,
    increment: (by: number) => set((state) => ({ value: state.value + by })),
    decrement: (by: number) => set((state) => ({ value: state.value - by })),
}));

export default useStore;
