import { useState, useRef, useEffect } from 'react';

import ApexCharts from 'apexcharts'

// import GaugeChart from 'react-gauge-chart';


interface MeterProps {
  value: number;
  max: number;
}




export function Meter(props: MeterProps) {
  var options = {
    chart: {
      height: 350,
      type: 'radialBar',
    },
    series: [props.value],
    colors: ['#008FFB'],
    plotOptions: {
      radialBar: {
        startAngle: -135,
        endAngle: 135,
        track: {
          background: '#e7e7e7',
          startAngle: -135,
          endAngle: 135,
        },
        dataLabels: {
          name: {
            show: false,
          },
          value: {
            fontSize: '22px',
            show: true,
          },
        },
      },
    },
    fill: {
      type: 'gradient',
      gradient: {
        shade: 'dark',
        type: 'horizontal',
        shadeIntensity: 0.5,
        gradientToColors: ['#87D4F9'],
        inverseColors: true,
        opacityFrom: 1,
        opacityTo: 1,
        stops: [0, 100],
      },
    },
    stroke: {
      lineCap: 'round',
    },
    labels: ['Frequency'],
  };
  return (
    new ApexCharts(document.querySelector('#chart'), options).render()
  )

}

const gaugeStyle = {
  maxWidth: "20vw",
  height: 250,
}

interface GaugeProps {
  val1?: number;
  val2?: number;
  val3?: number;
  unit1?: string;
  unit2?: string;
  unit3?: string;
}

//NOTE
//Input values must be converted to a percentage before being passed to a Gauge component

export default function GaugeModels(props: GaugeProps) {

  const values = [50, 73, 21, 35, 45, 93, 62, 75, 32, 9, 100];
  const [counter, setCounter] = useState(0);

  useEffect(() => {
    setTimeout(() => setCounter(counter + 1), 1000);
  }, [counter]);

  // console.log(values[counter % values.length]);
  return (
    <Meter max={100} />
  );
}
